<script>
    function toggleSubmenu(id) {
        if (id === "dropdown-bank") {
            const item = document.querySelector("#dropdown-bank");
            const caret = item.querySelector("#down");
            console.log(caret.style.display);
            if (caret.style.display === "block") {
                caret.style.display = "none";
                item.querySelector("#up").style.display = "block";
                document.getElementById("bank-submenu").style.display = "none";
            } else if (caret.style.display === "none") {
                caret.style.display = "block";
                item.querySelector("#up").style.display = "none";
                document.getElementById("bank-submenu").style.display = "block";
            }
        }
    }
</script>